package Assignment;

public class RightAnglePattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 1;
		 for(int i=0;i<10;i++)
		{
			for(int j=0;j<n;j++)
			{
				System.out.print("* ");
			}
			n=n+1;
			System.out.println();
		}

	}

}
